// Handle cases where an event begins or ends on a day other than this
if (stdate.compare(this.mDate) == -1) {
    startHour = 0;
    startMinute = 0;
}
if (enddate.compare(this.mDate) == 1) {
    endHour = 24;
    endMinute = 0;
}

